mapper.insert:

	SELECT FN_GET_SEQ('AR','TB_ARCV')

INSERT INTO TB_ARCV (
	 ARCV_NO   
	,ARCV_SBJT
	,ARCV_CNTT		
	,REG_DTT 
	,UPD_DTT 
	,REG_ID 
	,UPD_ID  
	,ATT_FL_PTH
	,ATT_FL_NM
) VALUES (
	 #{ARCV_NO}
	,#{ARCV_SBJT} 
	,#{ARCV_CNTT}		
	,NOW()
	,NOW()
	,#{REG_ID}
	,#{UPD_ID}
	,#{ATT_FL_PTH}
	,#{ATT_FL_NM}
)

mapper.update:
UPDATE TB_ARCV 
   SET 			 
	 ARCV_SBJT  = #{ARCV_SBJT}
	,ARCV_CNTT  = #{ARCV_CNTT}
	,ATT_FL_PTH= #{ATT_FL_PTH}
	,ATT_FL_PTH= #{ATT_FL_NM}
	,UPD_DTT    = NOW()
	,UPD_ID     = #{UPD_ID}
 WHERE ARCV_NO  = #{ARCV_NO}

UPDATE TB_ARCV 
   SET ATT_FL_PTH= #{ATT_FL_PTH}
      ,ATT_FL_NM  = #{ATT_FL_NM}
 WHERE ARCV_NO = #{ARCV_NO}

UPDATE TB_ARCV 
   SET VW_CNT = VW_CNT + 1
 WHERE ARCV_NO = #{ARCV_NO}
