mapper.insert:

	SELECT FN_GET_SEQ('FC','TB_FCLT')

INSERT INTO TB_FCLT (
	 FCLT_MGMT_NO 		   	
	,FCLT_NM 		
	,FCLT_EXPL      
	,STTN 
	,FCLT_LOC
	,FCLT_STTS_CD
	,NFC_TAG_ID  	      			
	,REG_DTT   		
	,UPD_DTT   		
	,REG_ID    		
	,UPD_ID    		
) VALUES (
	 #{FCLT_MGMT_NO}
	,#{FCLT_NM}
	,#{FCLT_EXPL}
	,#{STTN}
	,#{FCLT_LOC}
	,#{FCLT_STTS_CD}
	,#{NFC_TAG_ID}
	,NOW()
	,NOW()
	,#{REG_ID}
	,#{UPD_ID}
)

mapper.update:
UPDATE TB_FCLT
   SET 
	 FCLT_NM 			= #{FCLT_NM}
	,FCLT_EXPL      	= #{FCLT_EXPL}
	,FCLT_STTS_CD		= #{FCLT_STTS_CD}
	,UPD_DTT   			= NOW()
	,UPD_ID    			= #{UPD_ID}
 WHERE FCLT_MGMT_NO 	= #{FCLT_MGMT_NO}
