mapper.insert:

	SELECT FN_GET_SEQ('RS','TB_FCLT_RSRV')

INSERT INTO TB_FCLT_RSRV (
	 USR_NO   
	,RSRV_VSTN   	
	,RSRV_NO      	
	,FCLT_MGMT_NO
	,CNCL_YN
	,ENTP_NM
	,WRKR_NM
	,CNCL_DTT
	,WRK_CNTT	
	,REG_DTT   		
	,UPD_DTT   		
	,REG_ID    		
	,UPD_ID    		
) VALUES (
	 #{USR_NO}
	,#{RSRV_VSTN}
	,#{RSRV_NO}
	,#{FCLT_MGMT_NO}
	,#{CNCL_YN}
	,#{ENTP_NM}
	,#{WRKR_NM}
	,#{CNCL_DTT}
	,#{WRK_CNTT}
	,NOW()
	,NOW()
	,#{REG_ID}
	,#{UPD_ID}
)

mapper.update:
UPDATE TB_FCLT_RSRV
   SET 	
	 RSRV_VSTN 		= #{RSRV_VSTN}	
	,CNCL_YN		= #{CNCL_YN}
	,ENTP_NM		= #{ENTP_NM}
	,WRKR_NM		= #{WRKR_NM}
	,CNCL_DTT		= #{CNCL_DTT}
	,WRK_CNTT		= #{WRK_CNTT}
	,UPD_DTT   		= NOW()
	,UPD_ID    		= #{UPD_ID}	
 WHERE RSRV_NO	 	= #{RSRV_NO}

mapper.select:
SELECT R.USR_NO ,R.RSRV_VSTN ,R.RSRV_NO ,R.FCLT_MGMT_NO ,D.CNCL_YN ,R.ENTP_NM ,R.WRKR_NM ,R.CNCL_DTT ,R.WRK_CNTT ,R.REG_DTT ,R.UPD_DTT ,R.REG_ID ,R.UPD_ID ,F.FCLT_NM ,D.RSRV_DT ,D.TZ_TP_CD ,D.RSRV_DTL_NO ,D.RSRV_DT ,H.VST_NO FROM TB_FCLT_RSRV R INNER JOIN TB_FCLT_RSRV_DT D on D.RSRV_NO = R.RSRV_NO INNER JOIN TB_FCLT F on F.FCLT_MGMT_NO = R.FCLT_MGMT_NO LEFT OUTER JOIN TB_FCLT_VST_HIST H on H.RSRV_DTL_NO = D.RSRV_DTL_NO
R.USR_NO =#{userNo} AND D.CNCL_YN='N' AND R.FCLT_MGMT_NO =#{facilityiesNo}
ORDER BY D.RSRV_DT DESC LIMIT #{startRow},#{endRow}

mapper.update:
UPDATE TB_FCLT_RSRV
   SET			
	CNCL_YN		= #{CNCL_YN}	
	,CNCL_DTT = NOW()		
 WHERE RSRV_NO	 	= #{RSRV_NO}
