mapper.update:
UPDATE TB_PMC_MNGR
   SET 				
	 MNGR_NM	 = #{MNGR_NM}
	,PWD 		 = #{PWD}
	,CPHN 		 = #{CPHN}
	,PHN 		 = #{PHN}
	,CNFM_YN 	 = #{CNFM_YN}
	,CNFR_ID 	 = #{CNFR_ID}
	,CNFM_DTT 	 = #{CNFM_DTT}	
	,MNGR_EML 	 = #{MNGR_EML}
	,BLT 		 = #{BLT}
	,PST_CD 	 = #{PST_CD}		
	,UPD_DTT 	 = NOW()		
	,UPD_ID 	 = #{UPD_ID}
	,MNGR_STTS_CD= #{MNGR_STTS_CD}  	
 WHERE MNGR_NO 	 = #{MNGR_NO}

mapper.select:
SELECT
	 count(MNGR_NO)			
 FROM TB_PMC_MNGR

 MNGR_ID = #{managerId}
 BLT = #{department}

SELECT MNGR_NO ,MNGR_ID ,MNGR_NM ,PWD ,CPHN ,PHN ,CNFM_YN ,CNFR_ID ,CNFM_DTT ,MNGR_PRMS_NO ,MNGR_EML ,BLT ,PST_CD ,REG_DTT ,UPD_DTT ,REG_ID ,UPD_ID ,MNGR_STTS_CD FROM TB_PMC_MNGR
MNGR_ID = #{managerId} BLT = #{department}
ORDER BY MNGR_NO DESC LIMIT #{startRow},#{endRow}

