mapper.insert:

	SELECT FN_GET_SEQ('QA','TB_QNA')

INSERT INTO TB_QNA (
	 QNA_NO    
	,Q_USR_NO  
	,A_MNGR_NO
	,Q_SBJT 
	,Q_CNTT  
	,Q_ATC_FL_SPTH
	,A_SBJT
	,A_CNTT 
	,Q_ATC_FL_NM  
	,Q_DTT
	,A_DTT  			
	,REG_DTT 
	,UPD_DTT 
	,REG_ID 
	,UPD_ID  
	,Q_NM
	,Q_PHN
	,Q_EML    
) VALUES (
	 #{QNA_NO}  
	,#{Q_USR_NO}  
	,#{A_MNGR_NO}
	,#{Q_SBJT}
	,#{Q_CNTT}  
	,#{Q_ATC_FL_SPTH}
	,#{A_SBJT}
	,#{A_CNTT}
	,#{Q_ATC_FL_NM}  
	,#{Q_DTT}
	,#{A_DTT} 
	,NOW()
	,NOW()
	,#{REG_ID}
	,#{UPD_ID}
	,#{Q_NM}
	,#{Q_PHN}
	,#{Q_EML}   
)

mapper.update:
UPDATE TB_QNA
   SET 	
	 Q_SBJT 		= #{Q_SBJT}
	,Q_CNTT  		= #{Q_CNTT}
	,Q_ATC_FL_SPTH	= #{Q_ATC_FL_SPTH}
	,A_SBJT			= #{A_SBJT}
	,A_CNTT 		= #{A_CNTT}
	,Q_ATC_FL_NM  	= #{Q_ATC_FL_NM}  
	,Q_DTT			= #{Q_DTT}
	,A_DTT  		= #{A_DTT}
	,UPD_DTT  		= NOW()
	,UPD_ID    		= #{UPD_ID}
	,Q_NM			= #{Q_NM}
	,Q_PHN			= #{Q_PHN}
	,Q_EML			= #{Q_EML}   
 WHERE QNA_NO 		= #{QNA_NO}

mapper.select:
SELECT q.QNA_NO ,q.Q_USR_NO ,q.A_MNGR_NO ,q.Q_SBJT ,q.Q_CNTT ,q.Q_ATC_FL_SPTH ,q.A_SBJT ,q.A_CNTT ,q.Q_ATC_FL_NM ,q.Q_DTT ,q.A_DTT ,q.REG_DTT ,q.UPD_DTT ,q.REG_ID ,q.UPD_ID ,q.Q_NM ,q.Q_PHN ,q.Q_EML ,m.MNGR_ID FROM TB_QNA q LEFT OUTER JOIN TB_PMC_MNGR m on m.MNGR_NO = q.A_MNGR_NO
q.Q_USR_NO = #{userNo} AND q.Q_SBJT LIKE CONCAT('%',#{querySubject},'%') AND q.Q_CNTT LIKE CONCAT('%',#{queryContent},'%')
LIMIT #{startRow},#{endRow}

mapper.update:
UPDATE TB_QNA
   SET 				
	 A_SBJT			= #{A_SBJT}
	,A_CNTT 		= #{A_CNTT}			
	,A_DTT  		= NOW()  
 WHERE QNA_NO 		= #{QNA_NO}
